package meraprogramming;

public class CondtionalStatement9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int ch = 1;
		switch (ch)
		{
		case 1: System.out.println("Vote for JDS");
		break;
		case 2 : System.out.println("Vote for congress");
		break;
		case 3 : System.out.println("Vote for BJP");
		break;
		default : System.out.println("Vote for AAP");
		}
	}

}
